PyTorch Author = DanteSU 样例 train.py import time import click from datetime import datetime import torch # from torch import nn from torch.utils.data import DataLoader # from torch.utils.tensorboard import SummaryWriter # from sklearn.metrics import confusion_matrix from utils.loss import * from utils.model import * from utils.loader import * from utils.others import * def train(gpu_set, epoch, learning_rate, data_path, batch_size, loss, weight_decay='0.00001', model_name = 'dp_cnn', time_now = 'fake time'): # Load train and val data print('Loading data...') trainset, valset = load_train_data(data_path) # Get and print useful settings print('Getting settings...') train_data_size,val_data_size,n_classes,input_lenghth = get_setting(trainset, valset) # Using DataLoader to load data print('Setting dataloader...') train_dataloader = DataLoader(dataset=trainset, batch_size=batch_size, shuffle=True ) val_dataloader = DataLoader(dataset=valset, batch_size=batch_size, shuffle=True ) # Set pytorch device print('Setting device...') device = get_device_name(gpu_set) # Create the model print('Creating model network...') model = model_set(model_name,n_classes, batch_size,input_lenghth).to(device) # Creat loss function print('Creating loss function...') loss_fn = loss_set(loss,device) # create optimizer print('Creating optimizer...') # optimizer = torch.optim.SGD(model.parameters(), lr = learning_rate) optimizer = torch.optim.Adam(model.parameters(), lr=learning_rate, betas=(0.9, 0.999), eps=1e-08, weight_decay=weight_decay, amsgrad=False) # set some sets total_train_step = 0 # record training's number # total_val_step = 0 # record validating's number # use tensorboard # writer = SummaryWriter("logs_train") for i in range(epoch): epoch_start_time = iters_time = time.time() # timer for entire epoch # iters_time = time.time() print("------The {}th epoch starts------".format(i+1)) # train starts training(train_dataloader,device,model,loss_fn,optimizer,batch_size,total_train_step,iters_time) validating(val_dataloader,val_data_size,device,model,loss_fn,time_now) save_checkpoints(time_now,model,model_name,i) print('The time consumption of this code is : {} s'.format(time.time() - epoch_start_time)) # writer.close() @click.command() @click.option('-g', '--gpu_set', type=str, default='-1', help='gpu_set: e.g. 0 0,1,2, 0,2. use -1 for CPU') @click.option('-dp', '--data_path', type=str, default='data', help='path of input data') @click.option('-e', '--epoch', type=int, default=10, help='epoch') @click.option('-bs', '--batch_size', type=int, default=1, help='batch size') @click.option('-lr', '--learning_rate', type=float, default=1e-3, help='batch size') @click.option('-l', '--loss', type=str, default='ce', help='loss function') @click.option('-wd', '--weight_decay', type=float, default=1e-5, help='weight decay') @click.option('-m', '--model_name', type=str, default='lstm', help='eg. lstm') def main(gpu_set, data_path, epoch, batch_size,learning_rate, loss, weight_decay,model_name): total_time = time.time() time_now = datetime.now() record_parameter(gpu_set, epoch = epoch, learning_rate = learning_rate, data_path=data_path, batch_size = batch_size, loss = loss, weight_decay = weight_decay, model_name = model_name, time_now = time_now) train(gpu_set, epoch = epoch, learning_rate = learning_rate, data_path=data_path, batch_size = batch_size, loss = loss, weight_decay = weight_decay, model_name = model_name, time_now = time_now) run_time = time.time() - total_time record_run_time(run_time) if __name__ == '__main__': main() utils loader.py import torch from torch.utils.data import Dataset import numpy as np import time def load_train_data(data_path): print('preparing trainset...') trainset_time = time.time() trainset = TrainDataset(data_path=data_path) print('trainset time consumption is :', time.time()-trainset_time) print('preparing valset...') valset_time = time.time() valset = ValDataset(data_path=data_path) print('trainset time consumption is :', time.time()-valset_time) return trainset, valset class TrainDataset(Dataset): """ 下载数据、初始化数据，都可以在这里完成 """ def __init__(self, data_path): self.X_train = self.npy_loader(data_path+'/x_train.npy') self.y_train_onehot = self.npy_loader(data_path+'/y_train_onehot.npy') def __getitem__(self, index): return self.X_train[index].unsqueeze(0), self.y_train_onehot[index] # .unsqueeze(0) def __len__(self): return len(self.X_train) def npy_loader(self,path): return torch.from_numpy(np.load(path)) class ValDataset(Dataset): """ 下载数据、初始化数据，都可以在这里完成 """ def __init__(self, data_path): self.X_val = self.npy_loader(data_path+'/x_val.npy') self.y_val_onehot = self.npy_loader(data_path+'/y_val_onehot.npy') def __getitem__(self, index): return self.X_val[index].unsqueeze(0), self.y_val_onehot[index] # .unsqueeze(0) def __len__(self): return len(self.X_val) def npy_loader(self,path): return torch.from_numpy(np.load(path)) class TestDataset(Dataset): """ 下载数据、初始化数据，都可以在这里完成 """ def __init__(self, data_path): self.X_test = self.npy_loader(data_path+'/x_test.npy') self.y_test_onehot = self.npy_loader(data_path+'/y_test_onehot.npy') def __getitem__(self, index): return self.X_test[index].unsqueeze(0), self.y_test_onehot[index] # .unsqueeze(0) def __len__(self): return len(self.X_test) def npy_loader(self,path): return torch.from_numpy(np.load(path)) loss.py import sys import torch from torch import nn import torch.nn.functional as F from torch.autograd import Variable def loss_set(loss, device): if loss =='ce': return nn.CrossEntropyLoss() if loss == 'bce': return nn.BCELoss() if loss =='mlsm': return nn.MultiLabelSoftMarginLoss() if loss =='mlcce': return multilabel_categorical_crossentropy if loss =='fl1': return WeightedFocalLoss() if loss =='fl2': return focalBceLoss if loss == 'fl3': return focal_loss() if loss == 'fl4': return FocalLoss() else: print('loss name error!!!') return sys.exit(0) class WeightedFocalLoss(nn.Module): "Non weighted version of Focal Loss" def __init__(self, alpha=.25, gamma=2, device = 'cuda:2'): super(WeightedFocalLoss, self).__init__() self.alpha = torch.tensor([alpha, 1-alpha]).to(device) self.gamma = gamma def forward(self, inputs, targets): BCE_loss = F.binary_cross_entropy_with_logits(inputs, targets, reduction='none') targets = targets.type(torch.long) at = self.alpha.gather(0, targets.data.view(-1)) pt = torch.exp(-BCE_loss) F_loss = at*(1-pt)**self.gamma * BCE_loss return F_loss.mean() class GHM_Loss(nn.Module): def __init__(self, bins, alpha): super(GHM_Loss, self).__init__() self._bins = bins self._alpha = alpha self._last_bin_count = None def _g2bin(self, g): return torch.floor(g * (self._bins - 0.0001)).long() def _custom_loss(self, x, target, weight): raise NotImplementedError def _custom_loss_grad(self, x, target): raise NotImplementedError def forward(self, x, target): g = torch.abs(self._custom_loss_grad(x, target)) bin_idx = self._g2bin(g) bin_count = torch.zeros((self._bins)) for i in range(self._bins): bin_count[i] = (bin_idx == i).sum().item() N = x.size(0) nonempty_bins = (bin_count > 0).sum().item() gd = bin_count * nonempty_bins gd = torch.clamp(gd, min=0.0001) beta = N / gd return self._custom_loss(x, target, beta[bin_idx]) class GHMC_Loss(GHM_Loss): def __init__(self, bins, alpha,device='cuda:2'): super(GHMC_Loss, self).__init__(bins, alpha) self.device = device def _custom_loss(self, x, target, weight): return torch.sum((torch.nn.NLLLoss(reduce=False)(torch.log(x),target)).mul(weight.to(self.device).detach()))/torch.sum(weight.to(self.device).detach()) def _custom_loss_grad(self, x, target): x=x.to(self.device).detach() target=target.to(self.device) return torch.tensor([x[i,target[i]] for i in range(target.shape[0])])-target class focal_loss(nn.Module): def __init__(self, alpha=0.25, gamma=2, num_classes = '11', size_average=True): """ focal_loss损失函数, -α(1-yi)**γ *ce_loss(xi,yi) 步骤详细的实现了 focal_loss损失函数. :param alpha: 阿尔法α,类别权重. 当α是列表时,为各类别权重,当α为常数时,类别权重为[α, 1-α, 1-α, ....],常用于 目标检测算法中抑制背景类 , retainnet中设置为0.25 :param gamma: 伽马γ,难易样本调节参数. retainnet中设置为2 :param num_classes: 类别数量 :param size_average: 损失计算方式,默认取均值 """ super(focal_loss,self).__init__() self.size_average = size_average if isinstance(alpha,list): assert len(alpha)==num_classes # α可以以list方式输入,size:[num_classes] 用于对不同类别精细地赋予权重 print(" --- Focal_loss alpha = {}, 将对每一类权重进行精细化赋值 --- ".format(alpha)) self.alpha = torch.Tensor(alpha) else: assert alpha2: input = input.view(input.size(0),input.size(1),-1) # N,C,H,W => N,C,H*W input = input.transpose(1,2) # N,C,H*W => N,H*W,C input = input.contiguous().view(-1,input.size(2)) # N,H*W,C => N*H*W,C target = target.view(-1,1) logpt = F.log_softmax(input) logpt = logpt.gather(1,target) logpt = logpt.view(-1) pt = Variable(logpt.data.exp()) if self.alpha is not None: if self.alpha.type()!=input.data.type(): self.alpha = self.alpha.type_as(input.data) at = self.alpha.gather(0,target.data.view(-1)) logpt = logpt * Variable(at) loss = -1 * (1-pt)**self.gamma * logpt if self.size_average: return loss.mean() else: return loss.sum() def focalBceLoss(pred,mask,device='cuda:3'): # pred[pred1]=1 # BCE_loss = F.binary_cross_entropy_with_logits(pred, mask) BCE_loss = multilabel_categorical_crossentropy(pred, mask) alpha = 0.25 alpha = torch.tensor([alpha, 1 - alpha]).to(device) gamma = 2 targets = BCE_loss.type(torch.long) at = alpha.gather(0, targets.data.view(-1)) pt = torch.exp(-BCE_loss) F_loss = at * (1 - pt) ** gamma * BCE_loss smooth = 1e-5 pred = torch.sigmoid(pred) num = mask.size(0) pred = pred.view(num, -1) #view()的作用相当于numpy中的reshape，重新定义矩阵的形状。 #print(mask.shape) mask = mask.contiguous().view(num, -1) intersection = (pred * mask) # dice = (2. * intersection.sum(1) + smooth) / (pred.sum(1) + mask.sum(1) + smooth) # dice = 1 - dice.sum() / num # print(type(dice)) # # 因为返回1维tensor，你可以想象成只含有1个元素的列表，要使用[0]索引才返回一个float数 # print(type(dice.item())) # print(type(F_loss.item())) # # print(dice) #print(type(F_loss.item())) return 0.5 * F_loss.item() + BCE_loss def multilabel_categorical_crossentropy(y_pred,y_true): y_pred = (1 - 2 * y_true) * y_pred y_pred_neg = y_pred - y_true * 1e12 y_pred_pos = y_pred - (1 - y_true) * 1e12 zeros = torch.zeros_like(y_pred[..., :1]) y_pred_neg = torch.cat([y_pred_neg, zeros], dim=-1) y_pred_pos = torch.cat([y_pred_pos, zeros], dim=-1) neg_loss = torch.logsumexp(y_pred_neg, dim=-1) pos_loss = torch.logsumexp(y_pred_pos, dim=-1) return neg_loss + pos_loss model.py import sys import math import torch from torch import nn, Tensor def model_set(model_name,n_classes, batch_size,input_lenghth): if model_name == 'resnet': return ResNet18(n_classes) if model_name == 'lstm': return LSTM(input_lenghth, n_classes) else: print('model name error!!!') return sys.exit(0) # model 1 class ResNet(nn.Module): def __init__(self, ch_in, ch_out): super(ResNet, self).__init__() self.conv1 = nn.Conv2d(ch_in, ch_out, kernel_size=3, stride=1, padding=1) self.bn1 = nn.BatchNorm2d(ch_out) self.conv2 = nn.Conv2d(ch_out, ch_out, kernel_size=3, stride=1, padding=1) self.bn2 = nn.BatchNorm2d(ch_out) self.relu = nn.ReLU(inplace=True) self.extra = nn.Sequential() # 如果输入、输出维度不同，需转化后才能相加 if ch_out != ch_in: self.extra = nn.Sequential( nn.Conv2d(ch_in, ch_out, kernel_size=1, stride=1), nn.BatchNorm2d(ch_out) ) def forward(self, x): out = self.conv1(x) out = self.bn1(out) out = self.relu(out) out = self.conv2(out) out = self.bn2(out) out = self.extra(x) + out return out class ResNet18(nn.Module): def __init__(self,n_classes): super(ResNet18, self).__init__() self.conv1 = nn.Sequential( nn.Conv2d(1, 64, kernel_size=3, stride=1, padding=1), nn.BatchNorm2d(64) ) self.relu = nn.ReLU(inplace=True) # 4 block [b, 64, h, w] => [b, 1024, h, w] self.blk1 = ResNet(64, 128) self.blk2 = ResNet(128, 256) self.blk3 = ResNet(256, 512) self.blk4 = ResNet(512, 1024) # 注意最后全连接层维度，进去之前需要先打平 self.outlayer = nn.Linear(1024*30*50, n_classes) def forward(self, x): x = x.view(len(x),30,-1) x = x.unsqueeze(1) x = self.conv1(x) x = self.relu(x) x = self.blk1(x) x = self.blk2(x) x = self.blk3(x) x = self.blk4(x) x = x.view(x.size(0), -1) # 先打平，再进全连接 x = self.outlayer(x) return x # model 2 class LSTM(nn.Module): def __init__(self,INPUT_SIZE,n_classes): super(LSTM, self).__init__() self.rnn = nn.LSTM( # if use nn.RNN(), it hardly learns input_size=INPUT_SIZE, hidden_size=64, # rnn hidden unit num_layers=1, # number of rnn layer batch_first=True, # input & output will has batch size as 1s dimension. e.g. (batch, time_step, input_size) ) self.out = nn.Linear(64, n_classes) def forward(self, x): # x shape (batch, time_step, input_size) # r_out shape (batch, time_step, output_size) # h_n shape (n_layers, batch, hidden_size) # h_c shape (n_layers, batch, hidden_size) r_out, (h_n, h_c) = self.rnn(x, None) # None represents zero initial hidden state # choose r_out at the last time step out = self.out(r_out[:, -1, :]) return out other.py import torch import time from pathlib import Path def record_parameter(gpu_set, epoch, learning_rate, data_path, batch_size,loss, weight_decay, model_name, time_now): fp = open('log/log.txt','a') fp.write('-'*100 + '
