'.format(run_time)) fp.close() def get_setting(trainset, valset): # get useful settings train_data_size = len(trainset.X_train) val_data_size = len(valset.X_val) n_classes = len(trainset.y_train_onehot[0]) input_lenghth = len(trainset.X_train[0]) # print to see print("The length of train set is : {}".format(train_data_size)) print("The length of val set is : {}".format(val_data_size)) print("The number of classes is : {}".format(n_classes)) print("The length of each input is : {}".format(input_lenghth)) return train_data_size,val_data_size,n_classes,input_lenghth def get_device_name(gpu_set): if gpu_set == '-1': return torch.device('cpu') else: return torch.device('cuda:{}'.format(gpu_set)) def training(train_dataloader,device,model,loss_fn,optimizer,batch_size,total_train_step,iters_time): for data in train_dataloader: traffic, targets = data traffic = traffic.to(device) targets = targets.to(device) outputs = model(traffic) # 将训练的数据放入 # print(traffic.shape,outputs.shape,targets.shape) # print(outputs) # print(targets.float()) loss = loss_fn(outputs, targets.float()) # 得到损失值 optimizer.zero_grad() # 优化过程中首先要使用优化器进行梯度清零 loss.backward() # 调用得到的损失，利用反向传播，得到每一个参数节点的梯度 optimizer.step() # 对参数进行优化 total_train_step += batch_size # 上面就是进行了一次训练，训练次数 +1 # Print some information when the number of iterations divide by batch size equal 200 if total_train_step/batch_size % 200 == 0: print("Iterations: {}, time consumptions :{}, Loss: {}".format(total_train_step, time.time() - iters_time, loss)) iters_time = time.time() # writer.add_scalar("train_loss", loss.item(), total_train_step) def validating(val_dataloader,val_data_size,device,model,loss_fn,time_now): total_val_loss = 0 total_accuracy = 0 # 准确率 with torch.no_grad(): for data in val_dataloader: traffic, targets = data traffic = traffic.to(device) targets = targets.to(device) outputs = model(traffic) loss = loss_fn(outputs, targets.float()) # 这里的 loss 只是一部分数据(data) 在网络模型上的损失 total_val_loss = total_val_loss + loss # 整个测试集的loss accuracy = (outputs.argmax(1) == targets.argmax(1)).sum() # 分类正确个数 total_accuracy += accuracy # 相加 fp = open('log/log.txt','a') fp.write('acc of {} is : '.format(time_now)+str(total_accuracy / val_data_size)+'
') fp.close() print("The loss in the whole val dataset: {}".format(total_val_loss)) print("The acc of the whole val dataset: {}".format(total_accuracy / val_data_size)) # print('整体测试集上的混淆矩阵:{}'.format(confusion_matrix())) # writer.add_scalar("val_loss", total_val_loss) # writer.add_scalar("val_accuracy", total_accuracy / val_data_size, total_val_step) def save_checkpoints(time_now,model,model_name,i): name_time_part = str(time_now) name_time_part = name_time_part[5:10]+'|'+name_time_part[11:19] cp_path = Path('checkpoints/model_{}_{}'.format(model_name, name_time_part)) cp_path.mkdir(parents=True, exist_ok=True) torch.save(model, "checkpoints/model_{}_{}/model_{}.pth".format(model_name, name_time_part, i)) print("model has been saved.") DanteSU            updated 2022-07-03 10:12:08
